function mainController(o,n){o.formData={},n.get("/api/todos").success(function(n){o.todos=n,console.log(n)}).error(function(o){console.log("Error: "+o)}),o.createTodo=function(){n.post("/api/todos",o.formData).success(function(n){o.formData={},o.todos=n,console.log(n)}).error(function(o){console.log("Error: "+o)})},o.deleteTodo=function(r){n["delete"]("/api/todos/"+r).success(function(n){o.todos=n,console.log(n)}).error(function(o){console.log("Error: "+o)})}}var scotchTodo=angular.module("scotchTodo",[]);scotchTodo.controller("mainController",["$scope","$http",mainController]);